import{_ as e,o as a,c as i,R as t}from"./chunks/framework.W9JL_T3t.js";const f=JSON.parse('{"title":"Options when creating an application","description":"","frontmatter":{"head":[["meta",{"name":"og:site_name","content":"Vue webapp"}],["meta",{"name":"og:image","content":"/images/vue-webapp-logo.png"}],["meta",{"name":"twitter:image","content":"/images/vue-webapp-logo.png"}]]},"headers":[],"relativePath":"en/vue-webapp/options/index.md","filePath":"en/vue-webapp/options/index.md","lastUpdated":1702541074000}'),n={name:"en/vue-webapp/options/index.md"},o=t('<h1 id="options-when-creating-an-application" tabindex="-1">Options when creating an application <a class="header-anchor" href="#options-when-creating-an-application" aria-label="Permalink to &quot;Options when creating an application&quot;">​</a></h1><h2 id="available-options" tabindex="-1">Available options <a class="header-anchor" href="#available-options" aria-label="Permalink to &quot;Available options&quot;">​</a></h2><p>The following options are currently available for configuration:</p><ul><li>Header - top part in the application template</li><li>Footer - bottom part of the application template</li><li>Drawer - navigation bar</li><li>Navbar - navigation menu (inside the drawer).</li><li>Github Pages deploy Workflow - GitHub Actions script for build and deploy application to GitHub Pages</li><li>PWA - add Service worker and Manifest to the application</li><li>API module - abstraction layer for communication with backend APIs</li><li>JSON-RPC adapter for API module</li><li>Splash screen - splash screen during application startup to improve UX</li><li>Google analytics - code for connecting statistics</li><li>Open graph - meta tags for building snippets by social networks</li></ul><h2 id="available-by-default" tabindex="-1">Available by default <a class="header-anchor" href="#available-by-default" aria-label="Permalink to &quot;Available by default&quot;">​</a></h2><ul><li>Dark theme - dark theme. All plugins support theme switching</li><li>BaseIcon - icon component with the ability to dynamically specify a name</li><li>BaseToggle - toggle component</li></ul><h2 id="in-development" tabindex="-1">In development <a class="header-anchor" href="#in-development" aria-label="Permalink to &quot;In development&quot;">​</a></h2><ul><li>Layout - application layouts</li><li>Global Preloader</li><li>i18n - internationalization of the application using different variants of <code>i18n</code> libraries.</li></ul><h2 id="in-plans" tabindex="-1">In plans <a class="header-anchor" href="#in-plans" aria-label="Permalink to &quot;In plans&quot;">​</a></h2><ul><li>Online showcase for dynamic testing of different variants of application parts and functionality</li><li>Expanding the number of variants of layout, header, footer and other components</li><li>Auth - module framework for authentication on JWT tokens, including different variants of registration and login forms.</li><li>Auth integration with popular service providers (Firebase and others).</li><li>GitHub Actions for application deployment to various services (Vercel, Heroku, Firesbase) and other useful CI/CD scripts.</li><li>Check on startup for its new version and reboot to avoid browser cache issues</li><li>Themes - application themes (include both different color palettes and the ability to change other CSS parameters for deep customization of the template and components)</li></ul>',10),l=[o];function r(s,p,c,d,h,u){return a(),i("div",null,l)}const b=e(n,[["render",r]]);export{f as __pageData,b as default};
