import{_ as e,o as a,c as i,Q as t}from"./chunks/framework.254bf437.js";const m=JSON.parse('{"title":"Опции при создании приложения","description":"","frontmatter":{"head":[["meta",{"name":"og:site_name","content":"Vue webapp"}],["meta",{"name":"og:image","content":"/images/vue-webapp-logo.jpg"}],["meta",{"name":"twitter:image","content":"/images/vue-webapp-logo.jpg"}]]},"headers":[],"relativePath":"vue-webapp/options/index.md","filePath":"vue-webapp/options/index.md"}'),o={name:"vue-webapp/options/index.md"},l=t('<h1 id="опции-при-создании-приложения" tabindex="-1">Опции при создании приложения <a class="header-anchor" href="#опции-при-создании-приложения" aria-label="Permalink to &quot;Опции при создании приложения&quot;">​</a></h1><h2 id="доступные-опции" tabindex="-1">Доступные опции <a class="header-anchor" href="#доступные-опции" aria-label="Permalink to &quot;Доступные опции&quot;">​</a></h2><p>На данный момент доступны следующие опции:</p><ul><li>Layout - макет приложения</li><li>Header - верхняя часть в шаблоне приложения</li><li>Footer - нижняя часть в шаблоне приложения</li><li>Drawer - навигационное меню</li><li>BaseIcon - компонент иконки с возможностью динамичного указания имени</li><li>Github Action Workflow - GitHub Actions сценарий для билда и деплоя приложения на GitHub Pages</li><li>PWA - добавление Service worker и Манифеста к приложению</li></ul><h2 id="в-разработке" tabindex="-1">В разработке <a class="header-anchor" href="#в-разработке" aria-label="Permalink to &quot;В разработке&quot;">​</a></h2><ul><li>API модуль - абстрактный слой для связи с бэкенд API</li><li>Splash screen - экран-заставка во время старта приложения для улучшения UX</li><li>i18n - интернационализация приложения с помощью различных вариантов <code>i18n</code> библиотек.</li></ul><h2 id="в-планах" tabindex="-1">В планах <a class="header-anchor" href="#в-планах" aria-label="Permalink to &quot;В планах&quot;">​</a></h2><ul><li>Расширение вариантов layout, header, footer</li><li>JSON-RPC адаптер для API модуля</li><li>Auth - каркас модуля для аутентификации на JWT токенах, включая различные варианты форм регистрации и логина</li><li>Themes - темы приложения (включают в себя как разные цветовые палитры, так и возможности менять другие для глубокой кастомизации шаблона и вида компонентов CSS стили)</li><li>Проверка при старте на свою новую версию и перезагрузка для избежания проблем с кэшем браузера</li></ul>',8),n=[l];function r(s,c,d,h,p,u){return a(),i("div",null,n)}const b=e(o,[["render",r]]);export{m as __pageData,b as default};
